if (responseText.includes('critical') || responseText.includes('urgent') || responseText.includes('immediate')) {
  alertLevel = 'CRITICAL';
} else if (responseText.includes('high priority') || responseText.includes('significant risk') || responseText.includes('attention needed')) {
  alertLevel = 'HIGH';  
} else if (responseText.includes('medium') || responseText.includes('monitor') || responseText.includes('watch')) {
  alertLevel = 'MEDIUM';
}

return [{
  ...aiResponse,
  alert_level: alertLevel,
  timestamp: new Date().toISOString(),
