}];


return $input.all();

// Data Integration Agent - Real Supply Chain Data Processing
const items = $input.all();
let processedData = {
  inventory_data: [],
  inbound_data: [],
  outbound_data: [],
  material_master: [],
  data_quality: {
    total_records: 0,
    errors: [],
    warnings: [],
    critical_alerts: []
  },
  business_insights: {
    low_stock_materials: [],
    expired_batches: [],
    high_value_inventory: [],
    plant_utilization: {}
  }
};

// Process and categorize data
items.forEach(item => {
  const data = item.json;
  processedData.data_quality.total_records++;

  // Categorize by data source
  if (data.BALANCE_AS_OF_DATE) {
    // Inventory data processing
    let inventoryItem = { ...data };

    // Data validation
    if (!inventoryItem.MATERIAL_NAME || inventoryItem.MATERIAL_NAME === '') {
      processedData.data_quality.errors.push({
        type: 'missing_material_name',
        record: inventoryItem
      });
    }

    // Convert stock to number
    inventoryItem.UNRESTRICTED_STOCK = parseFloat(inventoryItem.UNRESTRICTED_STOCK) || 0;
    inventoryItem.STOCK_SELL_VALUE = parseFloat(inventoryItem.STOCK_SELL_VALUE) || 0;

    // Check for low stock (less than 1000 units)
    if (inventoryItem.UNRESTRICTED_STOCK < 1000 && inventoryItem.UNRESTRICTED_STOCK > 0) {
      processedData.business_insights.low_stock_materials.push({
        material: inventoryItem.MATERIAL_NAME,
        plant: inventoryItem.PLANT_NAME,
        current_stock: inventoryItem.UNRESTRICTED_STOCK,
        value: inventoryItem.STOCK_SELL_VALUE
      });
    }

    // Check for high value inventory (>100,000)
    if (inventoryItem.STOCK_SELL_VALUE > 100000) {
      processedData.business_insights.high_value_inventory.push({
        material: inventoryItem.MATERIAL_NAME,
        plant: inventoryItem.PLANT_NAME,
        value: inventoryItem.STOCK_SELL_VALUE,
        stock: inventoryItem.UNRESTRICTED_STOCK
      });
    }

    // Check for SCRAP batches
    if (inventoryItem.BATCH_NUMBER === 'SCRAP') {
      processedData.data_quality.warnings.push({
        type: 'scrap_inventory',
        material: inventoryItem.MATERIAL_NAME,
        plant: inventoryItem.PLANT_NAME,
        value: inventoryItem.STOCK_SELL_VALUE
      });
    }

    processedData.inventory_data.push(inventoryItem);

  } else if (data.INBOUND_DATE) {
    // Inbound data processing
    let inboundItem = { ...data };
    inboundItem.NET_QUANTITY_MT = parseFloat(inboundItem.NET_QUANTITY_MT) || 0;

    // Validate inbound date
    const inboundDate = new Date(inboundItem.INBOUND_DATE);
    if (isNaN(inboundDate.getTime())) {
      processedData.data_quality.errors.push({
        type: 'invalid_inbound_date',
        record: inboundItem
      });
    }

    processedData.inbound_data.push(inboundItem);

  } else if (data.OUTBOUND_DATE) {
    // Outbound data processing  
    let outboundItem = { ...data };
    outboundItem.NET_QUANTITY_MT = parseFloat(outboundItem.NET_QUANTITY_MT) || 0;

    processedData.outbound_data.push(outboundItem);

  } else if (data.POLYMER_TYPE) {
    // Material master processing
    processedData.material_master.push(data);
  }
});

// Calculate plant utilization
const plantStats = {};
processedData.inventory_data.forEach(item => {
  if (!plantStats[item.PLANT_NAME]) {
    plantStats[item.PLANT_NAME] = {
      total_materials: 0,
      total_value: 0,
      total_stock: 0
    };
  }
  plantStats[item.PLANT_NAME].total_materials++;
  plantStats[item.PLANT_NAME].total_value += item.STOCK_SELL_VALUE;
  plantStats[item.PLANT_NAME].total_stock += item.UNRESTRICTED_STOCK;
});
processedData.business_insights.plant_utilization = plantStats;

// Generate critical alerts
if (processedData.business_insights.low_stock_materials.length > 5) {
  processedData.data_quality.critical_alerts.push({
    type: 'multiple_low_stock',
    count: processedData.business_insights.low_stock_materials.length,
    message: `${processedData.business_insights.low_stock_materials.length} materials are running low on stock`
  });
}

// Summary
processedData.summary = {
  total_inventory_records: processedData.inventory_data.length,
  total_inbound_records: processedData.inbound_data.length,
  total_outbound_records: processedData.outbound_data.length,
  total_materials: processedData.material_master.length,
  data_quality_score: Math.max(0, 100 - (processedData.data_quality.errors.length * 10) - (processedData.data_quality.warnings.length * 5)),
  critical_alerts_count: processedData.data_quality.critical_alerts.length,
  processing_timestamp: new Date().toISOString()
};

return [processedData];

// Prepare comprehensive response to send back to orchestrator
const executionData = $input.first().json;
const purchaseOrder = executionData.purchase_order;
const executionResults = executionData.execution_results;

// Create response payload
const orchestratorResponse = {
  // Response metadata
  response_id: `RESP-REP-${Date.now()}`,
  agent_type: 'replenisher',
  orchestrator_id: executionData.orchestrator_id,
  original_request_id: executionData.request_id,
  response_timestamp: new Date().toISOString(),

  // Execution status
  success: executionResults.overall_status === 'SUCCESS',
  status: executionResults.overall_status,

  // Task completion details
  task_completion: {
    original_task: executionData.task_data,
    completion_status: executionResults.overall_status,
    processing_time_ms: new Date() - new Date(executionData.received_at),
    steps_completed: executionData.processing_status.steps_completed
  },

  // Replenishment results
  replenishment_results: {
    material: executionData.materials_info.primary_material,
    shortage_addressed: executionData.material_analysis.shortage_analysis.current_shortage,
    order_quantity: purchaseOrder.order_details.quantity,

    // Purchase order details
    purchase_order: {
      po_number: purchaseOrder.po_number,
      supplier: purchaseOrder.supplier.name,
      total_cost: purchaseOrder.order_details.total_cost,
      expected_delivery: purchaseOrder.delivery.expected_date,
      status: purchaseOrder.status,
      expedited: purchaseOrder.delivery.expedited
    },

    // Financial impact
    financial_impact: {
      cost: purchaseOrder.order_details.total_cost,
      expedite_cost: purchaseOrder.order_details.expedite_cost,
      cost_category: purchaseOrder.order_details.total_cost > 50000 ? 'HIGH' : 
                    purchaseOrder.order_details.total_cost > 10000 ? 'MEDIUM' : 'LOW'
    }
  },

  // Action summary
  actions_taken: executionResults.actions_executed.map(action => ({
    action: action.action,
    status: action.status,
    summary: action.details
  })),

  // Next steps and recommendations
  recommendations: {
    follow_up_required: purchaseOrder.status === 'PENDING_APPROVAL',
    monitoring_needed: true,
    estimated_resolution: purchaseOrder.delivery.expected_date,
    risk_factors: [
      ...(purchaseOrder.delivery.expedited ? ['Expedited delivery risk'] : []),
      ...(purchaseOrder.supplier.reliability_score < 0.9 ? ['Supplier reliability concern'] : []),
      ...(executionData.material_analysis.urgency_assessment.urgency_level === 'CRITICAL' ? ['Critical timeline'] : [])
    ]
  },

  // Metrics for orchestrator
  performance_metrics: {
    response_time_ms: new Date() - new Date(executionData.received_at),
    complexity_score: executionResults.metrics.total_actions,
    success_rate: executionResults.metrics.successful_actions / executionResults.metrics.total_actions,
    cost_efficiency: purchaseOrder.order_details.unit_cost <= 150 ? 'GOOD' : 'REVIEW_NEEDED'
  }
};

return [{ json: orchestratorResponse }];

// Execute the required actions for replenishment
const poData = $input.first().json;
const purchaseOrder = poData.purchase_order;
const nextActions = poData.next_actions;

// Track action results
const actionResults = [];
const executionLog = [];

// Process each action
for (const action of nextActions) {
  const actionStart = new Date();

  switch (action) {
    case 'CONTACT_SUPPLIER':
