          special_terms: purchaseOrder.delivery.expedited ? 'Rush delivery arranged' : 'Standard terms'
        },
        timestamp: actionStart.toISOString()
      };
      actionResults.push(contactResult);
      executionLog.push(`${actionStart.toISOString()}: Contacted ${purchaseOrder.supplier.name}`);
      break;

    case 'SUBMIT_ORDER':
      // Simulate order submission
      const submitResult = {
        action: 'SUBMIT_ORDER',
        status: 'SUCCESS',
        details: {
          po_number: purchaseOrder.po_number,
          submission_method: 'ERP_INTEGRATION',
          confirmation_number: `CONF-${Date.now().toString().slice(-6)}`,
          estimated_processing: '24 hours'
        },
        timestamp: actionStart.toISOString()
      };
      actionResults.push(submitResult);
      executionLog.push(`${actionStart.toISOString()}: Submitted PO ${purchaseOrder.po_number}`);
      purchaseOrder.status = 'SUBMITTED';
      break;

    case 'ESCALATE_TO_MANAGEMENT':
      // Simulate management escalation
      const escalateResult = {
        action: 'ESCALATE_TO_MANAGEMENT',
        status: 'SUCCESS',
        details: {
