      break;
  }
}

// Calculate completion status
const allActionsSuccessful = actionResults.every(result => 
  result.status === 'SUCCESS' || result.status === 'IN_PROGRESS'
);

const completionStatus = allActionsSuccessful ? 'SUCCESS' : 'PARTIAL_SUCCESS';

// Create execution summary
const executionSummary = {
  ...poData,
  execution_results: {
    overall_status: completionStatus,
    actions_executed: actionResults,
    execution_log: executionLog,
    completion_time: new Date().toISOString(),

    // Summary metrics
    metrics: {
      total_actions: nextActions.length,
      successful_actions: actionResults.filter(r => r.status === 'SUCCESS').length,
      pending_actions: actionResults.filter(r => r.status === 'IN_PROGRESS').length,
      failed_actions: actionResults.filter(r => r.status === 'FAILED').length
