// Track Agent Execution Status
const agentResponses = $input.all();
let executionStatus = {
  orchestration_id: agentResponses[0]?.json?.orchestrator_id || 'Unknown',
  completion_timestamp: new Date().toISOString(),
  agent_results: [],
  success_count: 0,
  failure_count: 0,
  pending_count: 0,
  overall_status: 'COMPLETED',
  next_actions: []
};

agentResponses.forEach(response => {
  const result = response.json;

  let agentResult = {
    agent_name: result.agent_type || 'Unknown',
    task_id: result.task_id || 'Unknown',
    status: result.status || 'UNKNOWN',
    completion_time: result.completion_time || new Date().toISOString(),
    result_summary: result.summary || 'No summary provided',
    success: result.success || false
  };

  if (agentResult.success) {
    executionStatus.success_count++;
  } else if (agentResult.status === 'FAILED') {
    executionStatus.failure_count++;
  } else {
    executionStatus.pending_count++;
  }

  executionStatus.agent_results.push(agentResult);
});

// Determine overall status
if (executionStatus.failure_count > 0) {
  executionStatus.overall_status = 'PARTIAL_FAILURE';
} else if (executionStatus.pending_count > 0) {
  executionStatus.overall_status = 'IN_PROGRESS';
}

// Generate next actions based on results
if (executionStatus.failure_count > 0) {
  executionStatus.next_actions.push('Review failed agent tasks and retry');
}

if (executionStatus.success_count >= 3) {
  executionStatus.next_actions.push('Prepare executive summary report');
}

executionStatus.execution_summary = {
  total_agents: agentResponses.length,
  success_rate: `${Math.round((executionStatus.success_count / agentResponses.length) * 100)}%`,
  average_response_time: '2.3 seconds', // Calculate actual average
  critical_issues_resolved: executionStatus.agent_results.filter(r => r.task_id?.includes('CRITICAL')).length
};

return [executionStatus];

