function extractMaterialFromTaskId(taskId) {
  const materials = [
    "Raw Material A",
    "Raw Material B", 
    "Component C",
    "Chemical D",
    "Packaging Material"
  ];

  // Use task ID to determine material (simple hash-based selection)
